import "../Intheatres/InTheatres.css"
import {useState,useEffect} from "react"
import {getfavourites} from "../services/Movies"
import Imovies from "../../modules/Imovies";

type props= {
  search:string
}

const Favourite = (prop:props) => {
    const [movies,setmovies]=useState<Imovies[]>([])
    useEffect (
     
      ()=>{
        const helper = async ()=>{
        const movies= await getfavourites();
        setmovies(movies);
        }
        helper()
      }
  
    );
  
    return (
      <div>
        <h2> Favourites</h2>
        <div className="flexbox">
        {
           
           movies.filter(
            item=>{
              return prop.search.toLowerCase()===""?item:item.title.toLowerCase().includes(prop.search.toLowerCase())
            }
          ).map(
            movie=>(
              <div key={ movie.id } className="imagecontainer">
                <NavLink  to={`${location}/${movie.id}`}></NavLink> <img className="poster" alt="" src={movie.posterurl} />  
                 <p>{movie.title}</p>
                 <button className="favourites">Remove From favourites </button>
  
              </div>
            )
          )
         } 
       </div>
       </div>
    );
  };
 
export default Favourite;
