import React, { useState } from 'react';
import { Route, Routes } from 'react-router';
import Favourites from './pages/FavouritesPage';
import MoviesinTheatres from './pages/Movies-In-TheatresPage';
import TopRatedIndia from './pages/Top-Rated-Indiapage';
import TopRatedMovies from './pages/Top-Rated-MoviesPage';
import MoviesComing from './pages/Movies-ComingPage';
import "./component/NavigationMenu.css"
import { NavLink } from 'react-router-dom';
function App() {
  const [search,setsearch]=useState("")

  return (
    
   <div className='app'>
    <div className="header">
      <div className="navigation">
      <h3><NavLink className="home" to="/">Movie Manager</NavLink></h3>
     <ul className="navbar">
       <li> <NavLink className="links" to="/theatres">In Theatres</NavLink> </li>
       <li> <NavLink className="links" to="/soon">Coming soon</NavLink> </li>
       <li> <NavLink className="links" to="/best">Top Rated</NavLink> </li>
       <li> <NavLink className="links" to="/india">Top Rated indian</NavLink> </li>
       <li> <NavLink className="links" to="/fav">Favourites</NavLink> </li>
       
     </ul>
     </div>
     <input className="search" onChange={(e)=>{
        setsearch(e.target.value)
     }} placeholder="search" />
     
     
    </div>
    <Routes>
      <Route path='/' element={<MoviesinTheatres movie={search} />} />
      <Route path='/theatres' element={<MoviesinTheatres movie={search} />} />
      <Route path='/soon' element={<MoviesComing moviec  />} />
      <Route path='/best' element={<TopRatedMovies />} />
      <Route path='/india' element={<TopRatedIndia />} />
      <Route path='/fav' element={<Favourites />} />
    </Routes>
   </div>
  );
}

export default App;
