  const response = await axios.get("http://localhost:3001/top-rated-movies");
  return response.data;
};
const gettopratedindian = async () => {
  const response = await axios.get("http://localhost:3001/top-rated-india");
  return response.data;
};
const getfavourites = async () => {
  const response = await axios.get("http://localhost:3001/favourite");
  return response.data;
};

export {
  getmovies,
  removemovie,
  getmoviedetails,
  getrunningnow,
  getcomingsoon,
  gettoprated,
  gettopratedindian,
  getfavourites,
  addtofavourites,
};
