import "../Intheatres/InTheatres.css"
import {useState,useEffect} from "react"
import {addtofavourites, gettoprated} from "../services/Movies"
import Imovies from "../../modules/Imovies";
import { NavLink } from "react-router-dom";

type Props= {
  search:string;
}

const TopRated = (prop:Props) => {
    const [movies,setmovies]=useState<Imovies[]>([])
    const [movie,setmovie]=useState<Imovies>()

    useEffect (
     
      ()=>{
        const helper = async ()=>{
        const movies= await gettoprated();
        setmovies(movies);
        }
        helper()
      }
      
  
,[]);
useEffect (
   
  ()=>{
    const helper = async ()=>{
    const added= await addtofavourites(movie);
    console.log(added)
    }
    helper()
  }

,);
    const location="top-rated-movies"
    return (
      <div>
        <h2> Top rated</h2>
        <div className="flexbox">
        {
           movies.filter(
            item=>{
              return prop.search.toLowerCase()===""?item:item.title.toLowerCase().includes(prop.search.toLowerCase())
            }
          ).map(
            movie=>(
              <div key={ movie.id } className="imagecontainer">
                <NavLink  to={`${location}/${movie.id}`}> <img className="poster" alt="" src={movie.posterurl} />  </NavLink>
                 <p>{movie.title}</p>
                 <button className="favourites" >Add To favourites </button>
  
              </div>
            )
          )
         } 
       </div>
       </div>
    );
  };
 
export default TopRated;
