import { useEffect, useState } from "react";
import { useParams } from "react-router";
import { getmoviedetails } from "../component/services/Movies";
import Imovies from "../modules/Imovies";
const Moviedetails = () => {
    const {location}=useParams()
     const {id} =useParams()
    const [movie,setmovie]=useState<Imovies>()
  useEffect (
   
    ()=>{
      const helper = async ()=>{
      const movies= await getmoviedetails(location,id);
      setmovie(movies);
      }
      helper()
    }

  ,[]);

    console.log(movie)
    return ( 
        <div>
            <img src={movie?.posterurl} alt=""></img>
            <div>
              <p>TITLE:<span>{movie?.title}</span></p>
              <p>year<span>{movie?.year}</span></p>
              <p>story<span>{movie?.storyline}</span></p>
              <p>actors<span>{movie?.actors}</span></p>
              
              
            </div>
        </div>
     );
}
 
export default Moviedetails;
