import "./InTheatres.css";
import {useState,useEffect} from "react"
import {addtofavourites, getrunningnow} from "../services/Movies"
import Imovies from "../../modules/Imovies";
import { type } from "os";
import { NavLink } from "react-router-dom";

type Props= {
  search:string;
}

const InTheatres = (prop:Props) => {

  const [movies,setmovies]=useState<Imovies[]>([])
  const [movie,setmovie]=useState<Imovies>()
     
  useEffect (
   
    ()=>{
      const helper = async ()=>{
      const movies= await getrunningnow();
      setmovies(movies);
      }
      helper()
    }

  ,[]);
  useEffect (
   
   ()=>{
      const helper = async ()=>{
      const added= await addtofavourites(movie);
      added.status
      }
      helper()
    }

  ,[movie]); 
 const location= "movies-in-theaters";
  return (
    <div>
      <h2> Running now</h2>
      <div className="flexbox">
      {
           

          movies.filter(
            item=>{
              return prop.search.toLowerCase()===""?item:item.title.toLowerCase().includes(prop.search.toLowerCase())
            }
          ).map( 
          movie=>(
              
            <div key={ movie.id }className="imagecontainer">
               <NavLink  to={`${location}/${movie.id}`}><img className="poster"  alt="" src={movie.posterurl} />  </NavLink>
               <p>{movie.title}</p>
               <button className="favourites" onClick={()=>
               {
                setmovie(movie)
               }
              }>Add To favourites </button>

            </div>
          )
        )
       } 
     </div>
     </div>
  );
};



export default InTheatres;
