import "../Intheatres/InTheatres.css"
import {useState,useEffect} from "react"
import {getfavourites, removemovie} from "../services/Movies"
import Imovies from "../../modules/Imovies";
import { NavLink } from "react-router-dom";

type props= {
  search:string
}

const Favourite = (prop:props) => {
    const [movies,setmovies]=useState<Imovies[]>([])
    const [movie,setmovie]=useState<Imovies>()
    const [response,setresponse]=useState<string>()

    useEffect (
     
      ()=>{
        const helper = async ()=>{
        const movies= await getfavourites();
        setmovies(movies);
        }
        helper()
      }
  
    );
    useEffect (
   
      ()=>{
         const helper = async ()=>{
          if( movie!==undefined )
          {
            const removed= await removemovie(movie);
            setresponse  
             }
            }
         helper()
       }
   
     ,[movie]); 
     const location="favourite"
    return (
      <div>
        <h2> Favourites</h2>
        <div className="flexbox">
        {
           
           movies.filter(
            item=>{
              return prop.search.toLowerCase()===""?item:item.title.toLowerCase().includes(prop.search.toLowerCase())
            }
          ).map(
            movie=>(
              <div key={ movie.id } className="imagecontainer">
                <NavLink  to={`${location}/${movie.id}`}><img className="poster" alt="" src={movie.posterurl} /></NavLink> 
                 <p>{movie.title}</p>
                 <button className="favourites" onClick={()=>
               {
                setmovie(movie)
               }
              } >Remove From favourites </button>
  
              </div>
            )
          )
         } 
       </div>
        {

        }
       </div>
    );
  };
 
export default Favourite;
