import axios from "axios"

const getmoviedetails=async(location:string | undefined,id:string)=>{
   const response=await axios.get(`http://localhost:3001/${location}/${id}`)
   return response.data
}

const getrunningnow= async()=>
{
    const response=await axios.get("http://localhost:3001/movies-in-theaters")
   return response.data
}
const getcomingsoon= async()=>
{
    const response=await axios.get("http://localhost:3001/movies-coming")
   return response.data
}
const gettoprated= async()=>
{
    const response=await axios.get("http://localhost:3001/top-rated-movies")
   return response.data
}
const gettopratedindian= async()=>
{
    const response=await axios.get("http://localhost:3001/top-rated-india")
   return response.data
}
const getfavourites= async()=>
{
    const response=await axios.get("http://localhost:3001/favourite")
   return response.data
}

export {
    getmoviedetails,
    getrunningnow,
    getcomingsoon,
    gettoprated,
    gettopratedindian,
    getfavourites
}
