import "../Intheatres/InTheatres.css"
import {useState,useEffect} from "react"
import {addtofavourites, gettoprated} from "../services/Movies"
import Imovies from "../../modules/Imovies";
import { NavLink } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";

type Props= {
  search:string;
}

const TopRated = (prop:Props) => {
    const [movies,setmovies]=useState<Imovies[]>([])
    const [movie,setmovie]=useState<Imovies>()

    useEffect (
     
      ()=>{
        const helper = async ()=>{
        const movies= await gettoprated();
        setmovies(movies);
        }
        helper()
      }
      
  
,[movies]);
useEffect (
   
  ()=>{
     
     const helper = async ()=>{
     if( movie!==undefined )
     {
       try {
       await addtofavourites(movie).then ((response)=>{
        toast.success('Added Successfully', {
          position: "top-right",
          autoClose: 1500,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "colored",
          });
       })}catch(e:any)
     {
      toast("Already Added")
     }}}
     helper()
     }

 ,[movie]); 


    const location="top-rated-movies"
    return (
      <div>
        <h2> Top rated</h2>
        <div className="flexbox">
        {
           movies.filter(
            item=>{
              return prop.search.toLowerCase()===""?item:item.title.toLowerCase().includes(prop.search.toLowerCase())
            }
          ).map(
            movie=>(
              <div key={ movie.id } className="imagecontainer">
                <NavLink  to={`${location}/${movie.id}`}> <img className="poster" alt="" src={movie.posterurl} />  </NavLink>
                 <p>{movie.title}</p>
                 <button className="favourites" onClick={()=>
               {
                setmovie(movie)
               }} >Add To favourites </button>
  
              </div>
            )
          )
         } 
       </div>
       <ToastContainer />

       </div>
    );
  };
 
export default TopRated;
