import { useEffect, useState } from "react";
import { useParams } from "react-router";
import { getmoviedetails } from "../component/services/Movies";
import Imovies from "../modules/Imovies";
import "../style/moviedetails.css"
const Moviedetails = () => {
  const { location } = useParams();
  const { id } = useParams();
  const [movie, setmovie] = useState<Imovies>();
  useEffect(() => {
    const helper = async () => {
      const movies = await getmoviedetails(location, id);
      setmovie(movies);
    };
    helper();
  }, []);

  console.log(movie);
  return (
    <div className="flex">
      <img className="posters" src={movie?.posterurl} alt=""></img>
      <div className="details">
        <div className="info">
          TITLE:<span className="value">{movie?.title}</span>
        </div>
        <div className="info">
          year<span>{movie?.year}</span>
        </div>
        <div className="info">
          story<span>{movie?.storyline}</span>
        </div>
      </div>
    </div>
  );
};

export default Moviedetails;
