import { type } from "os";
import { NavLink } from "react-router-dom";
import "../style/NavigationMenu.css";

// Here props and call back is used for sending serached value from child to parent

type Props = {
  callback: (x: string) => void;
};

const NavigationMenu = (prop: Props) => {
  const handlecallback = (set: string) => {
    prop.callback(set);
  };

  return (
    <div className="header">
      <div className="navigation">
        <h3>
          <a className="home" href="/theatres">
            Imovies
          </a>
        </h3>
        <ul className="navbar">
          <li>
            {" "}
            <NavLink className="links" to="/theatres">
              In Theatres
            </NavLink>{" "}
          </li>
          <li>
            {" "}
            <NavLink className="links" to="/soon">
              Coming soon
            </NavLink>{" "}
          </li>
          <li>
            {" "}
            <NavLink className="links" to="/best">
              Top Rated
            </NavLink>{" "}
          </li>
          <li>
            {" "}
            <NavLink className="links" to="/india">
              Top Rated indian
            </NavLink>{" "}
          </li>
          <li>
            {" "}
            <NavLink className="links" to="/fav">
              Favourites
            </NavLink>{" "}
          </li>
        </ul>
      </div>
      <input
        className="search"
        onChange={(e) => {
          handlecallback(e.target.value);
        }}
        placeholder="search"
      />
    </div>
  );
};

export default NavigationMenu;
