import {useState,useEffect} from "react"
import {addtofavourites, getrunningnow} from "../../services/Movies"
import Imovies from "../../modules/Imovies";
import { type } from "os";
import { NavLink } from "react-router-dom";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import "../style/mainbody.css"


type Props= {
  search:string;
}

const InTheatres = (prop:Props) => {

  const [movies,setmovies]=useState<Imovies[]>([])
  const [movie,setmovie]=useState<Imovies>()
  
  
     
  useEffect (
   
    ()=>{
      const helper = async ()=>{
      const movies= await getrunningnow();
      setmovies(movies);
      }
      helper()
    }

  ,[movies]);
  useEffect (
   
   ()=>{
      
      const helper = async ()=>{
      if( movie!==undefined )
      {
        try {
        await addtofavourites(movie).then ((response)=>{
          toast.success('Added Successfully', {
            position: "top-right",
            autoClose: 1500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "colored",
            });
        })}catch(e:any)
      {
        toast.error('Already Added', {
          position: "top-right",
          autoClose: 1500,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "colored",
          });
      }}}
      helper()
      }

  ,[movie]); 


 const location= "movies-in-theaters";
  return (
    <div>
      <h2> Running now</h2>
      <div className="flexbox">
      {
           

          movies.filter(
            item=>{
              return prop.search.toLowerCase()===""?item:item.title.toLowerCase().includes(prop.search.toLowerCase())
            }
          ).map( 
          movie=>(
              
            <div key={ movie.id }className="imagecontainer">
               <NavLink  to={`${location}/${movie.id}`}><img className="poster"  alt="" src={movie.posterurl} />  </NavLink>
               <p>{movie.title}</p>
               <button className="favourites" onClick={()=>
               {
                setmovie(movie)
               }
              }>Add To favourites </button>

            </div>
          )
        )
       } 
     </div>
     <ToastContainer />
     </div>
  );
};



export default InTheatres;
