import "./InTheatres.css";
import {useState,useEffect} from "react"
import {getrunningnow} from "../services/Movies"
const InTheatres = () => {

  const [movies,setmovies]=useState([])
  const [loading,setloading]=useState(true)
  useEffect (
   
    ()=>{
      const helper = async ()=>{
      const movies= await getrunningnow();
      setmovies(movies);
      setloading
      }
      helper()
    }

  );

  return (
    <div>
      <h2> Running now</h2>
      <div className="flexbox">
        <div className="imagecontainer">
          <img
            className="poster"
            alt=""
            src="https://images-na.ssl-images-amazon.com/images/M/MV5BMTg1MTY2MjYzNV5BMl5BanBnXkFtZTgwMTc4NTMwNDI@._V1_SY500_CR0,0,337,500_AL_.jpg"
          />
          <p>Mission Impossible fallout</p>
          <button className="favourites">Add To favourites </button>
        </div>
      </div>
    </div>
  );
};

export default InTheatres;
