import { Route, Routes } from 'react-router';
import Favourites from './pages/FavouritesPage';
import MoviesinTheatres from './pages/Movies-In-TheatresPage';
import TopRatedIndia from './pages/Top-Rated-Indiapage';
import TopRatedMovies from './pages/Top-Rated-MoviesPage';
import MoviesComing from './pages/Movies-ComingPage';
import "./component/NavigationMenu.css"
import { NavLink } from 'react-router-dom';
import Moviedetails from './pages/MoviesDetailsPage';
function App() {
  const [search,setsearch]=useState("")
  const [state,setstate]=useState("runningnow")
  return (
    
   <div className='app'>
    <div className="header">
      <div className="navigation">
      <h3 onClick={()=>{ setstate("runningnow") }}>Movie Manager</h3>
     <ul className="navbar">
       <li onClick={()=>{ setstate("runningnow") }}> In Theatres </li>
       <li onClick={()=>{ setstate("comingsoon") }}>Coming soon</li>
       <li onClick={()=>{ setstate("toprated") }}>Top Rated</li>
       <li onClick={()=>{ setstate("topratedindian") }}>Top Rated indian</li>
       <li onClick={()=>{ setstate("favourites") }}> Favourites </li>
       
     </ul>
     </div>
     <input className="search" onChange={(e)=>{
        setsearch(e.target.value)
     }} placeholder="search" />
     
     
    </div>
    {
      se
    }
   </div>
  );
}

export default App;
