import React, { useState } from 'react';
import { Route, Routes } from 'react-router';
import Favourites from './pages/FavouritesPage';
import MoviesinTheatres from './pages/Movies-In-TheatresPage';
import TopRatedIndia from './pages/Top-Rated-Indiapage';
import TopRatedMovies from './pages/Top-Rated-MoviesPage';
import MoviesComing from './pages/Movies-ComingPage';
import "./component/NavigationMenu.css"
import { NavLink } from 'react-router-dom';
import Moviedetails from './pages/MoviesDetailsPage';
function App() {
  const [search,setsearch]=useState("")
  const [state,setstate]=useState("runningnow")
  return (
    
   <div className='app'>
    <div className="header">
      <div className="navigation">
      <h3 className='home' onClick={()=>{ setstate("runningnow")
      doc }}>Movie Manager</h3>
     <ul className="navbar">
       <li className="links" onClick={()=>{ setstate("runningnow");  }}> In Theatres </li>
       <li className="links" onClick={()=>{ setstate("comingsoon") }}>Coming soon</li>
       <li className="links" onClick={()=>{ setstate("toprated") }}>Top Rated</li>
       <li className="links" onClick={()=>{ setstate("topratedindian") }}>Top Rated indian</li>
       <li className="links" onClick={()=>{ setstate("favourites") }}> Favourites </li>
       
     </ul>
     </div>
     <input className="search" onChange={(e)=>{
        setsearch(e.target.value)
     }} placeholder="search" /> 
     
     
    </div>
    {
      state==="runningnow" && <MoviesinTheatres movie={search} />
    }
    {
      state==="comingsoon" && <MoviesComing movie={search} />
    }
    {
      state==="toprated" && <TopRatedMovies movie={search} />
    }
    {
      state==="topratedindian" && <TopRatedIndia movie={search} />
    }
    {
      state==="favourites" && <Favourites movie={search} />
    }
   </div>
  );
}

export default App;
